CREATE TABLE `Role` (  `roleName` VARCHAR(50),  `roleId` SMALLINT,  PRIMARY KEY (roleId));CREATE TABLE `User` (  `userId` SMALLINT,  `username` VARCHAR(255),  `password` VARCHAR(255),  `email` VARCHAR(255) ,  PRIMARY KEY (userId));CREATE TABLE `User_roles` (  `userId` SMALLINT,  `roleId` SMALLINT,  PRIMARY KEY (userId, roleId),  FOREIGN KEY (userId) REFERENCES User(userId),  FOREIGN KEY (roleId) REFERENCES Role(roleId));CREATE TABLE `Address` (  `addressId` SMALLINT,  `userId` SMALLINT,  `streetAddress`  VARCHAR(255),  `city`   VARCHAR(255),  `state` VARCHAR(100),  `zip` INT(5),  `country` VARCHAR(100),    PRIMARY KEY (addressId),  FOREIGN KEY (userId) REFERENCES User(userId));CREATE TABLE `Storage` (  `storageId` SMALLINT,  `streetAddress` VARCHAR(255),  `city` VARCHAR(100),  `state` VARCHAR(100),  `zip` INT(5),  `country` VARCHAR(100),  PRIMARY KEY (storageId));CREATE TABLE `Product` (  `productId` SMALLINT,  `productName` VARCHAR(255),  `productStock` SMALLINT,  `productImage` VARCHAR(255),  `productPrice` DECIMAL,  `storageId` SMALLINT,  PRIMARY KEY (productId),  FOREIGN KEY (storageId) REFERENCES Storage(storageId));CREATE TABLE `Orders` (  `orderId` SMALLINT,  `userId` SMALLINT,  `trackingNumber` VARCHAR(255),  `totalPrice` DECIMAL ,  `productId`SMALLINT,  `shippingAddressId` SMALLINT,  `orderTime` TIMESTAMP,  `orderStatus` VARCHAR(100),  PRIMARY KEY (orderId),  FOREIGN KEY (userId) REFERENCES User(userId),  FOREIGN KEY (productId) REFERENCES Product(productId));CREATE TABLE `PaymentInfo` (  `paymentId` SMALLINT,  `billingAddressId` SMALLINT ,  `orderId` SMALLINT,  `cardHolderFirstName` VARCHAR(100) ,  `cardHolderLastName` VARCHAR(100) ,  `cardNumber`INT(16),    PRIMARY KEY (paymentId),    FOREIGN KEY (billingAddressId) REFERENCES Address(addressId),    FOREIGN KEY (orderId) REFERENCES Orders(orderId));
